<?phpsession_start();?><script>		function updateuser(id){		list=$('#ulist'+id);		var cur=list.html();		list.attr("name",cur);		var lod=$('#uploading'+id);		lod.show();		$('#updatebut'+id).attr("disabled",true).css('opacity',0.5); 		$.ajax({			type: 'post',			url: 'user account/updateuser.php',			data: {'id':id},			success: function(data){				$('#updatebut'+id).removeAttr("disabled");				$('#ulist'+id).html(data);			},			error:function(){				lod.hide();				$('#updatebut'+id).removeAttr("disabled").css('opacity',1); 			}		});  	}		//cancel update user	function cancelupdate(id){ 		var cur=$('#ulist'+id).attr("name"); 		$('#ulist'+id).html(cur); 	}		//save update user	function saveupdateuser(id){ 		var uname=$('#uname'+id).val(); 		var username=$('#username'+id).val(); 		var status=$('#status'+id).val();		var designation=$('#designation'+id).val();		$.ajax({			type: 'POST',			url: 'user account/saveupdateuser.php',			data: {'id':id,'uname':uname, 'username':username,'status':status,'designation':designation},			success: function(data){			$('#ulist'+id).html(data);			},			error:function(){			alert('error');			}		});  	}		//add new user	function adduser(){	var addlod=$('#adddloading');		var name=$('#nname').val();	var username=$('#nusername').val();	var department=$('#ndepartment').val();	var password=$('#npassword').val();	var addbut = $('#adduserbut');		var i=$('.ucheck').attr('name');	if((name=="")||(username=="")||(password=="")){		$('#addusercon input').each(function(){			if($(this).val()==""){				$(this).css("border","1px solid red").attr("placeholder","You can't leave this empty");			}		});	}else if(i==1){		$('#overlay,#box').show();		$('#boxmsg').html("Username is already taken.<br>Please choose another one!");	}else if(i==2){		$('#overlay,#box').show();		$('#boxmsg').html("Username is not yet verified.<br>Please wait!");	}else{		addlod.show();		addbut.attr('disabled',true);		$.ajax({			type: 'POST',			url: 'adduser.php',			data: {'name':name,'department':department,'username':username,'password':password},			success: function(data){			addlod.hide();			$('.ulist:first').before(data);			$('#addusercon input').val("").css("border","1px solid #c2c0c0").removeAttr("placeholder");;			$('#ucheckcon img,#ucheckmsg').hide();			addbut.removeAttr('disabled');			},			error:function(){			addlod.hide();			addbut.removeAttr('disabled');			}		});	}			}		//showing the add user form	function showadduser(){ 		if( $('#addusercon:visible').length==1 ){		$('#addusercon').fadeOut();		}else{		$('#addusercon').fadeIn();		}	}		//check for username is already in use	// var xhr;	function checkusername(a){				if(a!=""){				$('.ucheck,#ucheckmsg').hide().attr("name",2);				$('#ucheckload').show();				// if((xhr) && (xhr.readyState != 4)){					// xhr.abort();				// }				$.ajax({				type: 'POST',				url: 'checkusername.php',				data: {'username':a},				success: function(data){					if(data==1){						$('.ucheck').attr("name",1).hide(); 						$('#error').show();					}					else{						$('#error,#ucheckmsg,#ucheckload').hide();						$('#check').show();												$('.ucheck').attr("name",0); 					}									},				error:function(){				checkusername(a)				}			});		}else{		$('.ucheck').hide();		}	}		//showing the username check msg	function ucheckmsg(){	var count=$('.ucheck').attr('name');		if(count==1){			$('#ucheckmsg span').html("Username is already taken.");		}else{			$('#ucheckmsg span').html("Username is ok to use.");		}		$('#ucheckmsg').toggle().delay(2000).fadeOut();		 	}	//closing the prompt box	function boxclose(){	$('#overlay,#box').hide();	}		//searching user	var xhr2;	function searchuser(a){				if(a!=""){				if((xhr2) && (xhr2.readyState != 4)){					xhr2.abort();				}				$('#usersuggest').html("<tr><td colspan='4'><img src='img/loading2.gif' style='height:17px;margin:0 auto;display:block'></td></tr>").show();				xhr2=$.ajax({				type: 'POST',				url: 'user account/searchuser.php',				data: {'name':a},				success: function(data){				$('#usersuggest').html(data);									},				error:function(){				$('#usersuggest').html("<tr><td style='text-align:center'>Connection failed!</td>/tr>").show();				}			});		}else{		$('#usersuggest').hide();		}	}		//selecting user in suggusted search		function selectsearch(a){			$.ajax({				type: 'POST',				url: 'user account/selectsearch.php',				data: {'id':a},				success: function(data){				$('.ulist,#usersuggest').hide();				// $('#ulist'+a).remove();				$('.searchcon').html(data).attr("id","ulist"+a); 				$('#searchuser').val(""); 				$('#logresult').hide(); 				$('#viewall,#userlist').show(); 												}			});		}				//show all user		function viewall(){		$('.ulist').show();		$('#viewall').hide();		}		function log(a){ 		$.ajax({			type:'post',			url:'user account/log.php',			data:{'user_id':a},			success:function(data){				$('#viewall,#userlist').hide();				$('#logresult').html(data).show();			},			error:function(){				connection();			}		});	}		</script></script><style type="text/css">	#userlist input,#userlist select{padding:2px;}</style>					<div id="contentheader"> 												<div id="headeroption">							<!-- <span id="adduser" onclick="showadduser()">Add new user</span> --> 						</div>												<div id="searchusercon">							<input type="text" id="searchuser" onkeyup="searchuser(this.value)" placeholder="Search user by name" >							 <img  src="img/lens.png" id="lens">														<table id="usersuggest"></table>													</div>																</div>					<table id="addusercon" >							<tr>							<td>Name:</td><td><input type="text" id="nname"></td><td>Username:</td>							<td><input type="text" id="nusername" onkeyup="checkusername(this.value)" style="width:165px;padding:5px 25px 5px 5px;float:left">								<span id="ucheckcon">									<img src='img/error.png' onmouseover="ucheckmsg()" class='ucheck' id="error">									<img src='img/check.png' onmouseover="ucheckmsg()" class='ucheck' id="check">									<img src='img/loading2.gif' class='ucheck' id="ucheckload">								<div id="ucheckmsg" style="display:none;border:1px solid red;border-radius:5px;padding:5px;position:absolute;background:white;right:45px;top:-26px;">								<span></span>								<div style="position:absolute;background:url(img/pointer.png);bottom:-8px;right:15px;height:8px;width:12px">								</div>								</div>								</span>							</td>							</td>						</tr>												<tr>							<td>Password:</td><td><input type="text" id="npassword"></td><td>Department:</td>							<td>								<select id="ndepartment">									<option>CCJE</option>									<option>CEAF</option>									<option>CAF</option>									<option>CBA</option>								</select>							</td>						</tr>						<tr>							<td></td><td></td><td></td>							<td>								<button  onclick="adduser()" id="adduserbut"></button>								<img src="img/loading.gif" id="addloader">							</td>						</tr>					</table>										<?php					include('../dbconfig.php');										$user=mysql_query("select * from user order by user_id desc");					?>					<table id="userlist">						<tr>							<th>Name</th><th>Username</th><th>Password</th><th>Status</th><th>Designation</th><th>Action</th> 						</tr> 						<tr class="searchcon">						 						</tr> 						<tr class="ulist">						</tr>						<?php						while($row=mysql_fetch_array($user)){						?>												<tr class="ulist" id="ulist<?=$row['user_id'];?>">							<td ><?=$row['name'];?></td>							<td><?=$row['username'];?></td>							<td style="text-align:center"><span>******</span><input style="display:none" type="password" id="resetpassword"> <img onclick="reset(<?=$row['user_id'];?>)" style="cursor:pointer" src="img/reset.png" title="Reset Password">							<input type="submit" value="Reset" onclick="savereset(<?=$row['user_id'];?>)" style="display:none;cursor:pointer;background:#44bcd2;color:white">							</td>														<td>								<?php								if($row['status']==1){								echo "Activated";								}else{								echo "Deactivated";								}								?>							</td>							<td style="text-align:center">								<?=$row['type'];?>							</td>							<td class="action">								<div class="actionwrap">									<img src="img/loading.gif" class="uploading" id="uploading<?=$row['user_id'];?>">									<?php if($row['type']!="admin"){?>									<button style="float:left;" class="updateuserbut" id="updatebut<?=$row['user_id'];?>" onclick="updateuser(<?=$row['user_id'];?>)"></button>									<button onclick="log(<?=$row['user_id'];?>)" id="log" ></button>																		<?php }else{										?>									<button onclick="log(<?=$row['user_id'];?>)" id="log" style="float:right;right:5px;position:relative"></button>										<?php										} ?>									</div>							</td> 						</tr>						<?php						}						?>											  					</table> 					<div> 					<div id="logresult"> 						 					</div>					 <button id="viewall" name="user account/useraccount" onclick="menu(this.name)">View all </button> <script>function savereset(a){	var pass=$('#ulist'+a+ " td:eq(2) input[type=password]").val();	$.ajax({		type:'post',		url:'user account/savereset.php',		data:{'password':pass,'user_id':a},		success:function(data){			alert("Done resetting.");			$('#ulist'+a+ " td:eq(2) input").hide();			$('#ulist'+a+ " td:eq(2) span").show();			$('#ulist'+a+ " td:eq(2) img").show();		} ,			error:function(){			connection();		}	})}function reset(a){	$('#ulist'+a+ " td:eq(2) span").hide();	$('#ulist'+a+ " td:eq(2) img").hide();	$('#ulist'+a+ " td:eq(2) input").show();}</script>